import { DataService } from 'src/app/api/data.service';
import { Injectable } from '@angular/core';
import { PiWebApiService } from '../api/pi-webapi.service';
import { catchError } from 'rxjs/operators';

export interface PiDataFilter {
    facility: { repName?: string; sectionName: string; value: number };
    //portfolioId: string;
    //facilityOrPortfolio: 'facility' | 'portfolio'
    status: string;
    startDate: Date;
    endDate: Date;
    interval: number;
  }
  
  export interface TimelineChartDataPoint {
    locationName: string;
    tag: string;
    // statusValue:number,
    chillerStatusLabel: string;
    statusColor: string;
    startDate: Date;
    endDate: Date;
  }
  
  export interface RoomDisplayField {
    name: string;
    value: string;
    displayType?: string;
  }
  
  export interface TimelineChartData {
    points: TimelineChartDataPoint[];
    locations: { [name: string]: any };
    // locationCount: number;
    locationType: 'room' | 'facility';
  }
  
  export interface StatusPoint {
    timeStamp: number;
    numeric_value: number;
  }
  
  export type locationStatusLookup = { [name: string]: string };
  export type roomInfoLookup = { [name: string]: string };
  export type apfLimitsLookup = { [facRoomKey: string]: any };
  
  @Injectable()
  export class FacilityTimelineDashboardService {
    constructor(private dataService: DataService, private piWebApiService:PiWebApiService) {
      const defaultStartDate = new Date();
      defaultStartDate.setDate(defaultStartDate.getDate() - 1);

      this._piDataFilter$ = new Subject<PiDataFilter>()
      // new BehaviorSubject<PiDataFilter>({
      //   facility: { repName: '', sectionName: '', value: 0 },
      //   portfolioId: '',
      //   facilityOrPortfolio: 'facility',
      //   status: '',
      //   startDate: defaultStartDate,
      //   endDate: defaultEndDate,
      //   interval: 10
      // });
  
      this._ic$ = new BehaviorSubject<string>('');
      this._facilityFilterOptions$ = new BehaviorSubject<
        { repName: string; sectionName: string; value: string }[]
      >([]);
      this._svgMap$ = new BehaviorSubject<SvgMap>({
        name: 'apf_facility_all',
        backgroundSvg: '',
        id: 0,
        svgMapPins: [],
        svgMapArrows: [],
        viewbox: '0 0 0 0',
        defs: '',
        facilityId: 0
      });
      this._svgMapBackgroundImageUrl$ = new BehaviorSubject<string>('');
      this._currentStatusValues$ = new BehaviorSubject<LocationCurrentStatus[]>(
        []
      );
      this._parameterValues$ = new BehaviorSubject<Room[]>([]);
      this._timelineChartData$ = new BehaviorSubject<TimelineChartData>({
        points: [],
        locations: {},
        //locationCount:0,
        locationType: 'facility'
      });
      this._selectedPin$ = new BehaviorSubject<string>('');
      this._selectedRoomInfo$ = new BehaviorSubject<roomInfoLookup>({});
      this._hoveredPin$ = new BehaviorSubject<string>('');
      this._hoveredTimelineLabel$ = new BehaviorSubject<string>('');
      this._apfLimits$ = new BehaviorSubject<apfLimitsLookup>({});
      this._pinStates = new BehaviorSubject<locationStatusLookup>({});
    
      // Setup APF Limits from PI for all facilities
      //this.dataService.apfLimits().subscribe((limits: any[]) => {
      this.piWebApiService.apfLimits().subscribe((limits: any[]) => {

        const limitsLookup:{[key:string]:string} = {};

        try {
          for(const limit of limits) {
            if(limit.Conn_Room) {
              const name1 = limit.Conn_Room ? `${limit.Room}_${limit.Conn_Room}_DP` : limit.Room;
              const name2 = limit.Conn_Room ? `${limit.Conn_Room}_${limit.Room}_DP` : limit.Room;
              limitsLookup[`${limit.Facility.toLowerCase()}|${name1.toLowerCase()}`] = limit;
              limitsLookup[`${limit.Facility.toLowerCase()}|${name2.toLowerCase()}`] = limit;
            }
            else {
              limitsLookup[`${limit.Facility.toLowerCase()}|${limit.Room.toLowerCase()}`] = limit
            }
          }
        }
        catch(e){
          console.log(e)
        }
  
        this._apfLimits$.next(limitsLookup);
      });
  
      // create an observable of the facility selected in the fillter control
      const selectedFacility$ = this._piDataFilter$.pipe(
        distinctUntilChanged(
          (prev, curr) => prev.facility.value === curr.facility.value
        ),
        map((f) => f.facility)
      );

      const selectedStatus$ = this._piDataFilter$.pipe(
        map((f) => (f.status.toLowerCase())),
        distinctUntilChanged()
      );
      
      // create an observable of the marker type to use with the map - 'pin' or 'arrow'
      const svgMapMarker$ = this._piDataFilter$.pipe(
        map((f) => (f.status.toLowerCase() === 'dp' ? 'arrow' : 'pin')),
        distinctUntilChanged()
      );
  
      // Update the floor plan and room parameter info needed by svg map when the facility changes
      selectedFacility$
        .pipe(
          filter(facility=>Boolean(facility?.value)),
          mergeMap((facility) =>
            zip(
              of(facility.value),
              this.dataService.roomParameterInfo(facility.value) // parameter info from database for each room and attribute in facility
            )
          )
        )
        .subscribe(([facilityId, parameterValues]) => {
          const backGroundImageUrl = `assets/images/orig-floor-plans/FID${facilityId}_FloorPlan.jpg`;
          this._svgMapBackgroundImageUrl$.next(backGroundImageUrl);

          this._parameterValues$.next(parameterValues);
  
          this._selectedPin$.next('');
          this._selectedRoomInfo$.next({});
        });
      

      // Update the room states when the facility or the selected status attribute changes
      combineLatest([selectedFacility$, selectedStatus$])
        .pipe(
          mergeMap(([facility,status]) => {
            return this.piWebApiService.facilityCurrentStatusData(facility.sectionName, status);
          }),
          map((statusData)=>{
            const pinStatusLookup = reduce(
              statusData,
              (acc, x) => ({
                ...acc,
                [x.locationName]: this.statusColor(x.statusPoint.numeric_value)
              }),
              {}
            );
            return pinStatusLookup;
          })
        ).subscribe((pinStatusLookup)=>{
          this._pinStates.next(pinStatusLookup);
    
        })
        
  
      // Get the pins or arrows for the Svg Floor plan if the facility changes or attriibute changes between not dp and dp
      combineLatest([selectedFacility$, svgMapMarker$])
        .pipe(
          mergeMap(([facility, marker]) => {
            return this.dataService.svgMap(facility.value, marker);
          })
        )
        .subscribe((svgMap) => {
          this._svgMap$.next(svgMap);
        });
  
      // when a map pin is selected, prepare room info display data using the apf limits query, current status values, and the room parameters
      const roomCurrentAttributeData$ = 
        combineLatest([selectedFacility$, this._selectedPin$])
          .pipe(
            filter(([facility,pin])=>Boolean(pin)),
            mergeMap(([facility,pin])=>zip(
              of(facility),
              of(pin),
              //this.dataService.roomCurrentAttributeData(facility.value,pin)))
              this.piWebApiService.roomCurrentAttributeData(facility.sectionName,pin)))
          )

      roomCurrentAttributeData$
      .subscribe(([facility,pin,roomStatusValues]) => {
        const sectionName = facility.sectionName.toLowerCase();
        const key = `${sectionName}|${pin.toLowerCase()}`; // pin = room number
        const apfLimits = this._apfLimits$.value[key];
        const isDP = pin.indexOf('DP') > -1;
        const room = isDP ? 
          this._parameterValues$.value.find(
            (r) => (r.formattedName.toLowerCase() === pin.toLowerCase())
          ) : 
          this._parameterValues$.value.find(
            (r) => (r.roomNumber.toLowerCase() === pin.toLowerCase())
          );
  
        const info = {
          ...apfLimits,
          gsf: room?.sq,
          roomParameters: room?.roomParameters,
          roomStatusValues,
          compositeStatus: roomStatusValues.find(
            (x) => x.attribute === 'Composite'
          )?.statusPoint,
          tempStatus: roomStatusValues.find((x) => x.attribute === 'Temp')
            ?.statusPoint,
          rhStatus: roomStatusValues.find((x) => x.attribute === 'Hum')
            ?.statusPoint,
          achStatus: roomStatusValues.find((x) => x.attribute === 'Airx')
            ?.statusPoint,
          dpStatus: roomStatusValues.find((x) => x.attribute === 'DP')
            ?.statusPoint
        };
  
        this._selectedRoomInfo$.next(info);
      });
  

      // prepare data for specific facility timeline
      this._piDataFilter$
        .pipe(
          mergeMap((filter: PiDataFilter) =>

            this.piWebApiService.timelineData(
              filter.facility.sectionName,
              filter.status,
              filter.startDate,
              filter.endDate,
              filter.interval
            )
              .pipe(
                catchError((err) => {
                  console.log(
                    'Error from dataService.facilityRoomsTimelineDate:' +
                      JSON.stringify(err)
                  );
                  return of([]);
                }),
                map((data) => ({ filter, data }))
              )
          )
        )
        .subscribe((dataAndFilter) => {
          const chartDataPoints: TimelineChartDataPoint[] = [];
          const rooms = dataAndFilter.data.map((d) => d.location);
          const roomLookup = keyBy(rooms, (r) => r.roomNumber);
          const timestamps = (dataAndFilter.data || [])
            .filter((x) => x.points.some(Boolean))
            .map((x) => x.points[0].timestamp);
          const minTimestamp = timestamps.reduce(function (a, b) {
            return a < b ? a : b;
          },Number.MAX_VALUE);
  
          for (const x of dataAndFilter.data) {
            if (!x.points.some(Boolean)) {
              x.points = [
                {
                  timestamp: minTimestamp,
                  numeric_value: 1
                },
                {
                  timestamp: dataAndFilter.filter.endDate.getTime(),
                  numeric_value: 1
                }
              ];
            }
  
            x.points.sort((a, b) => a.timestamp - b.timestamp);
            let startTime = x.points[0].timestamp;
            for (const y of x.points) {
              if (y.timestamp < dataAndFilter.filter.startDate.getTime()) {
                console.log('error - timestamp before request time');
              }
              const point: TimelineChartDataPoint = {
                locationName: x.location.roomNumber,
                tag: x.tag,
                startDate: new Date(startTime),
                endDate: new Date(Math.max(y.timestamp, startTime)),
                statusColor: this.statusColor(y.numeric_value),
                chillerStatusLabel: this.chillerStatusLabel(y.numeric_value)
              };
  
              startTime = y.timestamp;
              chartDataPoints.push(point);
            }
          }

          const locations:any = {};
          for(const d of dataAndFilter.data) {
            locations[d.location.roomNumber] =d.location
          }

          this._timelineChartData$.next({
            points: chartDataPoints,
            locations: roomLookup,
            //locationCount:dataAndFilter.data.length,
            locationType: 'room'
          });
        });
    } // end contstructor
  
    private _ic$: Subject<string>;
    public setIC(ic: string) {
      this._ic$.next(ic);
    }
  
    private _piDataFilter$: Subject<PiDataFilter>;
    public filterPiData(filter: PiDataFilter) {
      this._piDataFilter$.next(filter);
    }
    public get piDataFilter$() {
      return this._piDataFilter$ as Observable<PiDataFilter>;
    }
  
    public get isFacilityAll$() {
      return this._piDataFilter$.pipe(
        map((f) => {
          return Number(f.facility) === 0;
        })
      );
    }
  
    private _svgMap$: Subject<SvgMap>;
    public get svgMap$() {
      return this._svgMap$ as Observable<SvgMap>;
    }
  
    private _currentStatusValues$: BehaviorSubject<LocationCurrentStatus[]>;
    private _pinStates: BehaviorSubject<locationStatusLookup>;
  
    public get pinStates$() {
      return this._pinStates as Observable<locationStatusLookup>;
    }
  
    private _parameterValues$: BehaviorSubject<Room[]>;
    public get parameterValues$() {
      return this._parameterValues$ as Observable<Room[]>;
    }
  
    private _timelineChartData$: Subject<TimelineChartData>;
    public get timelineChartData$() {
      return this._timelineChartData$ as Observable<TimelineChartData>;
    }
  
    private _svgMapBackgroundImageUrl$: Subject<string>;
    public get svgMapBackgroundImageUrl$() {
      return this._svgMapBackgroundImageUrl$ as Observable<string>;
    }
  
    private _selectedPin$: Subject<string>;
    public get selectedPin$() {
      return this._selectedPin$ as Observable<string>;
    }
  
    private _hoveredPin$: Subject<string>;
    public get hoveredPin$() {
      return this._hoveredPin$ as Observable<string>;
    }
  
    private _hoveredTimelineLabel$: Subject<string>;
    public get hoveredTimelineLabel$() {
      return this._hoveredTimelineLabel$ as Observable<string>;
    }
  
    private _selectedRoomInfo$: Subject<roomInfoLookup>;
    public get selectedRoomInfo$() {
      return this._selectedRoomInfo$ as Observable<roomInfoLookup>;
    }
  
    public setSelectedPin(pinName: string) {
      this._selectedPin$.next(pinName);
    }
  
    public setHoveredPin(pinName: string) {
      this._hoveredPin$.next(pinName);
    }
  
    public setHoveredTimelineLabel(label: string) {
      this._hoveredTimelineLabel$.next(label);
    }
  
    private _apfLimits$: BehaviorSubject<apfLimitsLookup>;
    public get apfLimits$() {
      return this._apfLimits$ as Observable<apfLimitsLookup>;
    }
  
    private statusColor = (statusVal: number) => {
      switch (statusVal) {
        case 0:
          return 'green';
        case 1:
          return 'gray';
        case 2:
          return 'yellow';
        case 3:
          return 'red';
        default:
          return 'lightgray';
      }
    };
  
    private chillerStatusLabel = (statusVal: number) => {
      switch (statusVal) {
        case 0:
          return 'Within Spec';
        case 1:
          return 'Comm Loss';
        case 2:
          return 'Warning';
        case 3:
          return 'Alarm (out of Spec)';
        default:
          return '';
      }
    };
  
    public gsfGrowthByClassification$ = this.dataService
      .gsfGrowthByClassification()
      .pipe(
        map((d: any) =>
          d.map((x: any) => {
            const goLiveDate = new Date(x.goLiveDate);
            return [
              goLiveDate,
              x.cncRoomsArea + x.iso8RoomsArea + x.iso7RoomsArea,
              x.criticalEnvironmentParametersCount
            ];
          })
        )
      );
  
    private _facilityFilterOptions$: BehaviorSubject<
      { repName: string; sectionName: string; value: string }[]
    >;
    public get facilityFilterOptions$() {
      return this._facilityFilterOptions$ as Observable<
        { repName: string; sectionName: string; value: string }[]
      >;
    }
  }

import {
  FacilityTimeSeriesData,
  LocationCurrentStatus,
  LocationTimeSeriesData,
  Room,
  RoomTimeSeriesData,
} from './models';
import { Observable, map, of } from 'rxjs'

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class PiWebApiService {
  constructor(private http: HttpClient) { }

  private HEADERS: Object = { withCredentials: true, accept: 'text/json' };

  private get<T>(url: string) {
    return this.http.get<T>(`${environment.piWebApi}/${url}`, this.HEADERS);
  }

  private post<T>(url: string, body: any) {
    return this.http.post<T>(`${environment.piWebApi}/${url}`, body, this.HEADERS);
  }

  timelineDataComposite(
    facility: string,
    startDate: Date,
    endDate: Date,
    interval: number) {
    const postBody = {
      "GetFacilityId": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements?path=\\\\ORF-COGENAF\\cGMP\\cGMP\\${facility}`
      },
      "GetRooms": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements/{0}/elements`,

        "ParentIds": [
          "GetFacilityId"
        ],
        "Parameters": [
          "$.GetFacilityId.Content.WebId"
        ]
      },

      "GetTimeline": {
        "Method": "GET",
        "ParentIds": ["GetRooms"],
        "RequestTemplate": {
          "Resource": `${environment.piWebApi}/streamsets/{0}/interpolated?startTime=${startDate.toISOString()}&endTime=${endDate.toISOString()}&interval=${interval}m&nameFilter=Status`
        },
        "Parameters": [
          "$.GetRooms.Content.Items[*].WebId"
        ]

      }
    }
    const roomTimelines = this.post<any>(
      `batch`, postBody
    ).pipe(map(x => {
      const roomData = x.GetTimeline.Content.Items.map((d: any) => {
        const data = d.Content.Items[0];
        const roomNumber = data.Path.split('|')[0].split('\\').slice(-1)[0];
        const room = { roomNumber, roomName: '', sq: 0 }  // TODO
        return { location: room, tag: data.Path, points: data.Items.map((itm: any) => ({ timestamp: new Date(itm.Timestamp), numeric_value: itm.Value })) };
      });
      return roomData;
    }));

    return roomTimelines as Observable<LocationTimeSeriesData<Room>[]>;
  }

  timelineDataDp(
    facility: string,
    startDate: Date,
    endDate: Date,
    interval: number
  ): Observable<LocationTimeSeriesData<Room>[]> {

    const postBody = {
      "GetFacilityId": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements?path=\\\\ORF-COGENAF\\cGMP\\cGMP\\${facility}`
      },
      "GetRooms": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements/{0}/elements`,

        "ParentIds": [
          "GetFacilityId"
        ],
        "Parameters": [
          "$.GetFacilityId.Content.WebId"
        ]
      },

      "GetDPRooms": {
        "Method": "GET",
        "ParentIds": ["GetRooms"],
        "RequestTemplate": {
          "Resource": `${environment.piWebApi}/elements/{0}/elements`
        },
        "Parameters": [
          "$.GetRooms.Content.Items[*].WebId"
        ]
      },

      "GetDPRoomsDP": {
        "Method": "GET",
        "ParentIds": ["GetDPRooms"],
        "RequestTemplate": {
          "Resource": "https://orf-cogenaf.ors.nih.gov/piwebapi/elements/{0}/attributes?nameFilter=DP"
        },
        "Parameters": [
          "$.GetDPRooms.Content.Items[*].Content.Items[*].WebId"
        ]
      },

      "GetTimeline": {
        "Method": "GET",
        "ParentIds": ["GetDPRoomsDP"],
        "RequestTemplate": {
          "Resource": `${environment.piWebApi}/streamsets/{0}/interpolated?startTime=${startDate.toISOString()}&endTime=${endDate.toISOString()}&interval=${interval}m&nameFilter=Status`
        },
        "Parameters": [
          "$.GetDPRoomsDP.Content.Items[*].Content.Items[0].WebId"
        ]

      }

    }

    const roomTimelines = this.post<any>(
      `batch`, postBody
    ).pipe(map(x => {
      const roomData = [];
      const roomNumbers: { [roomNumber: string]: boolean } = {}
      for (const itm of x.GetTimeline.Content.Items) {
        const data = itm.Content.Items[0];
        const roomNumber = data.Path.split('|')[0].split('\\').slice(-1)[0];
        if (!roomNumbers[roomNumber]) {
          roomNumbers[roomNumber] = true;
          const room = { roomNumber, roomName: '', sq: 0 }  // TODO
          roomData.push({
            location: room,
            tag: data.Path,
            points: data.Items.map((itm: any) => ({ timestamp: new Date(itm.Timestamp), numeric_value: itm.Value }))
          });
        }
      }
      return roomData;
      // const roomData = x.GetTimeline.Content.Items.map((d:any)=>{ 
      //   const data = d.Content.Items[0]; 
      //   const roomNumber = data.Path.split('|')[0].split('\\').slice(-1)[0]; 
      //   const room = { roomNumber, roomName:'', sq:0 }  // TODO
      //   return {location:room, tag:data.Path, points:data.Items.map((itm:any)=>({timestamp:new Date(itm.Timestamp),numeric_value:itm.Value}))};});
      // return roomData;
    }));

    return roomTimelines as Observable<LocationTimeSeriesData<Room>[]>;
  }

  timelineDataNonDp(
    facility: string,
    attr: string,
    startDate: Date,
    endDate: Date,
    interval: number
  ): Observable<LocationTimeSeriesData<Room>[]> {

    const postBody = {
      "GetFacilityId": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements?path=\\\\${environment.piServer}\\cGMP\\cGMP\\${facility}`
      },
      "GetRooms": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements/{0}/elements`,

        "ParentIds": [
          "GetFacilityId"
        ],
        "Parameters": [
          "$.GetFacilityId.Content.WebId"
        ]
      },
      "GetStatus": {
        "Method": "GET",

        "ParentIds": ["GetRooms"],

        "RequestTemplate": {
          "Resource": `${environment.piWebApi}/attributes?path=\\\\${environment.piServer}\\cGMP\\cGMP\\${facility}\\{0}|${attr}`
        },

        "Parameters": [
          "$.GetRooms.Content.Items[*].Name"
        ]
      },
      "GetTimeline": {
        "Method": "GET",
        "ParentIds": ["GetStatus"],
        "RequestTemplate": {
          "Resource": `${environment.piWebApi}/streamsets/{0}/interpolated?startTime=${startDate.toISOString()}&endTime=${endDate.toISOString()}&interval=${interval}m&nameFilter=Status`
        },
        "Parameters": [
          "$.GetStatus.Content.Items[*].Content.WebId"
        ]

      }
    };

    const roomTimelines = this.post<any>(
      `batch`, postBody
    ).pipe(map(x => {
      const roomData = x.GetTimeline.Content.Items.map((d: any) => {
        const data = d.Content.Items[0];
        const roomNumber = data.Path.split('|')[0].split('\\').slice(-1)[0];
        const room = { roomNumber, roomName: '', sq: 0 }  // TODO
        return { location: room, tag: data.Path, points: data.Items.map((itm: any) => ({ timestamp: new Date(itm.Timestamp), numeric_value: itm.Value })) };
      });
      return roomData;
    }));

    return roomTimelines as Observable<LocationTimeSeriesData<Room>[]>;
  }

  timelineData(
    facility: string,
    attr: string,
    startDate: Date,
    endDate: Date,
    interval: number
  ): Observable<LocationTimeSeriesData<Room>[]> {
    const attrLower = attr.toLowerCase();
    return attrLower === 'dp' ?
      this.timelineDataDp(facility, startDate, endDate, interval) :
      attrLower === 'sum all' ?
        this.timelineDataComposite(facility, startDate, endDate, interval) :
        this.timelineDataNonDp(facility, attr, startDate, endDate, interval);
  }

  facilityAlltimelineData(
    portfolioId: string,
    startDate: Date,
    endDate: Date,
    interval: number
  ):Observable<FacilityTimeSeriesData[]> {

    const postBody = {
      "cgmp": {
        "Method": "GET",
        "Resource": "https://orf-cogenaf.ors.nih.gov/piwebapi/elements?path=\\\\ORF-COGENAF\\cGMP\\cGMP"
      },
      "facilities": {
        "Method": "GET",
        "Resource":"https://orf-cogenaf.ors.nih.gov/piwebapi/elements/{0}/elements",
        "ParentIds":["cgmp"],
        "Parameters":["$.cgmp.Content.WebId"]
      },
    
      "timelines": {
        "Method": "GET",
        "ParentIds": ["facilities"],
    
        "RequestTemplate": {
          "Resource":"https://orf-cogenaf.ors.nih.gov/piwebapi/streamsets/{0}/interpolated?startTime=*-2d&endTime=*&interval=60m&nameFilter=facility_status_check"
        },
        "Parameters":["$.facilities.Content.Items[*].WebId"]
      }
    };

    const portfolioFacilities:{[portfolioId:string]:string[]} = {
      CC: ['PET_1','PET_3','2J','E_TER','DLM_SL','IIVAU'],
      CCE: ['2J','E_TER'],
      CCPharmacy: ['IIVAU','P_IVAU'],
      CCOther: ['PET_1','PET_3','DLM_SL'],
      NCI:['VVF','HPP','T30','Tr1','Tr2'],
      NIAID:[]
    }


    const timelines = this.post<any>(
      `batch`, postBody
    ).pipe(map(x => {
      const outerItems = x.timelines.Content.Items as any[];
      const facilityChecks = outerItems.filter(y=>y.Content.Items.some(Boolean)).map((y:any)=>y.Content.Items[0]);
      const timeSeriesData = facilityChecks.map((y:any)=>{
        try {
          const path = y.Path;   //'\\\\ORF-COGENAF\\cGMP\\cGMP\\2J|Facility_Status_Check'
          const i = path.indexOf('|');
          const elementPath = path.substring(0, i);
          const facilityName = elementPath.split('\\').slice(-1)[0];
          const points = (y.Items || []).map((z:any)=>{
            return {
              numeric_value:z.Value,
              timestamp:new Date(z.Timestamp).valueOf()
            }
          })
          return {
            facility: {
              facilityName
            },
            points,
            tag:elementPath
          }
        }
        catch(e){
          console.log(e);
          return {} as FacilityTimeSeriesData;
        }

      });
      const chosenFacilities = portfolioFacilities[portfolioId];
      const facilityTimeSeriesData = timeSeriesData.filter(y=>chosenFacilities.some(z=>z === y.facility.facilityName))
      return facilityTimeSeriesData as FacilityTimeSeriesData[];
    }));


    return timelines;
  }


  private extractStatus(item: any): LocationCurrentStatus {
    //const item = outItem.Content.Items[0];
    const path = item.Path;
    const valueObj = item.Value;
    const i = path.indexOf('|');
    const elementPath = path.substring(0, i);
    const attrPath = path.substring(i + 1);
    const room = elementPath.split('\\').slice(-1)[0];
    const statusName = attrPath.split('|')[0];
    const value = valueObj.Value;
    const timestamp = new Date(valueObj.Timestamp).valueOf();
    const attribute = statusName == 'Status' ? 'Composite' : statusName; // as ('Composite' | 'Temp' | 'Hum' | 'Airx' | 'DP')
    return {
      locationName: room,
      attribute: attribute,
      statusPoint: {
        timestamp,
        numeric_value: value
      }
    };
  }

  facilityCurrentCompositeData(facility:string): Observable<LocationCurrentStatus[]> {
    const postBody = {
      "GetFacilityId": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements?path=\\\\ORF-COGENAF\\cGMP\\cGMP\\${facility}`
      },

      "GetRooms": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements/{0}/elements`,

        "ParentIds": [
          "GetFacilityId"
        ],
        "Parameters": [
          "$.GetFacilityId.Content.WebId"
        ]
      },

      "GetCompositeStatus": {
        "Method": "GET",

        "ParentIds": ["GetRooms"],

        "RequestTemplate": {
          "Resource": `${environment.piWebApi}/streamsets/{0}/value?nameFilter=Status`
        },

        "Parameters": [
          "$.GetRooms.Content.Items[*].WebId"
        ]
      }
    }

    const currentStatuses = this.post<any>(
      `batch`, postBody
    ).pipe(map(x => {

      const outerCompositeItems = x.GetCompositeStatus.Content.Items;
      const innerCompositeItems = outerCompositeItems.map((item: any) => this.extractStatus(item.Content.Items[0]));

      return innerCompositeItems;

    }));

    return currentStatuses;
  }

  facilityCurrentDpData(facility:string): Observable<LocationCurrentStatus[]> {
    const postBody = {
      "GetFacilityId": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements?path=\\\\ORF-COGENAF\\cGMP\\cGMP\\${facility}`
      },

      "GetRooms": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements/{0}/elements`,

        "ParentIds": [
          "GetFacilityId"
        ],
        "Parameters": [
          "$.GetFacilityId.Content.WebId"
        ]
      },

      "GetDPRooms": {
        "Method": "GET",
        "ParentIds": ["GetRooms"],
        "RequestTemplate": {
          "Resource": "https://orf-cogenaf.ors.nih.gov/piwebapi/elements/{0}/elements"
        },
        "Parameters": [
          "$.GetRooms.Content.Items[*].WebId"
        ]
      },

      "GetDPRoomsDP": {
        "Method": "GET",
        "ParentIds": ["GetDPRooms"],
        "RequestTemplate": {
          "Resource": "https://orf-cogenaf.ors.nih.gov/piwebapi/elements/{0}/attributes?nameFilter=DP"
        },
        "Parameters": [
          "$.GetDPRooms.Content.Items[*].Content.Items[*].WebId"
        ]
      },

      "GetDPStatus": {
        "Method": "GET",

        "ParentIds": ["GetDPRoomsDP"],

        "RequestTemplate": {
          "Resource": "https://orf-cogenaf.ors.nih.gov/piwebapi/streamsets/{0}/value?nameFilter=Status"
        },

        "Parameters": [
          "$.GetDPRoomsDP.Content.Items[*].Content.Items[*].WebId"
        ]
      }

    }

    const currentStatuses = this.post<any>(
      `batch`, postBody
    ).pipe(map(x => {

      const outerDpItems = x.GetDPStatus.Content.Items || [];
      const innerDpItems = outerDpItems.map((item: any) => this.extractStatus(item.Content.Items[0]));

      return innerDpItems

    }));

    return currentStatuses;
  }
  
  facilityCurrentSpecificAttributeData(facility:string, status:string): Observable<LocationCurrentStatus[]> {
    const postBody = {
      "GetFacilityId": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements?path=\\\\ORF-COGENAF\\cGMP\\cGMP\\${facility}`
      },
      
      "GetRooms": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements/{0}/elements`,
    
        "ParentIds": [
                     "GetFacilityId"
            ],
        "Parameters": [
                     "$.GetFacilityId.Content.WebId"
            ]
      },
      
      "GetAttributes": {
        "Method": "GET",
    
        "ParentIds":["GetRooms"],
        
        "RequestTemplate":{
          "Resource": `${environment.piWebApi}/elements/{0}/attributes?nameFilter=${status}`
          },
        
        "Parameters":[
          "$.GetRooms.Content.Items[*].WebId"
          ]
      },
      
      "GetAttributeStatus": {
        "Method": "GET",
        "ParentIds":["GetAttributes"],
        "RequestTemplate":{
          "Resource": `${environment.piWebApi}/streamsets/{0}/value?nameFilter=Status`
        },
        "Parameters":[
          "$.GetAttributes.Content.Items[*].Content.Items[*].WebId"
        ]
      }
    }
      
      const currentStatuses = this.post<any>(
        `batch`, postBody
      ).pipe(map(x => {
  
        const outerItems = x.GetAttributeStatus.Content.Items || [];
        const innerItems = outerItems.map((item: any) => this.extractStatus(item.Content.Items[0]));
  
        return innerItems
  
      }));

      return currentStatuses;
    
  }
  

  facilityCurrentStatusData(facility: string, status:string): Observable<LocationCurrentStatus[]> {
    const statusLower = status.toLowerCase();
    
    if(statusLower === 'composite' || statusLower === 'sum all') {
      return this.facilityCurrentCompositeData(facility);
    }
    
    if(statusLower === 'dp') {
      return this.facilityCurrentDpData(facility); 
    }
    return this.facilityCurrentSpecificAttributeData(facility, status);
  }

  roomCurrentAttributeDataDP(facility: string, room: string, dpRoom: string) {
    const postBody =
    {
      "GetRoomDP": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/attributes?path=\\\\ORF-COGENAF\\cGMP\\cGMP\\${facility}\\${room}\\${dpRoom}|DP`
      },

      "GetDPStatus": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/streamsets/{0}/value?nameFilter=Status`,
        "ParentIds": [
          "GetRoomDP"
        ],
        "Parameters": [
          "$.GetRoomDP.Content.WebId"
        ]
      }
    }

    const currentStatus = this.post<any>(
      `batch`, postBody
    ).pipe(map(x => {
      const item = x.GetDPStatus.Content.Items[0];
      const status = this.extractStatus(item);
      return status;
    }));

    return currentStatus;
  }

  roomCurrentAttributeData(facility: string, roomFormattedName: string): Observable<LocationCurrentStatus[]> {

    if(roomFormattedName.toLowerCase().indexOf('_dp')> 0){
      return of([{
          locationName: roomFormattedName,
          attribute: 'DP',
          statusPoint: {
            timestamp:0,
            numeric_value: 0
          }
      }]);
    }
    
    const postBody = {
      "GetRoomId": {
        "Method": "GET",
        "Resource": `${environment.piWebApi}/elements?path=\\\\ORF-COGENAF\\cGMP\\cGMP\\${facility}\\${roomFormattedName}`
      },

      "GetCompositeStatus": {
        "Method": "GET",

        "ParentIds": ["GetRoomId"],

        "Resource": `${environment.piWebApi}/streamsets/{0}/value?nameFilter=Status`,

        "Parameters": [
          "$.GetRoomId.Content.WebId"
        ]
      },

      "GetAttributes": {
        "Method": "GET",

        "ParentIds": ["GetRoomId"],


        "Resource": `${environment.piWebApi}/elements/{0}/attributes`,

        "Parameters": [
          "$.GetRoomId.Content.WebId"
        ]
      },

      "GetAttributeStatus": {
        "Method": "GET",
        "ParentIds": ["GetAttributes"],
        "RequestTemplate": {
          "Resource": `${environment.piWebApi}/streamsets/{0}/value?nameFilter=Status`
        },
        "Parameters": [
          "$.GetAttributes.Content.Items[*].WebId"
        ]
      }

    }

    const currentStatuses = this.post<any>(
      `batch`, postBody
    ).pipe(map(x => {

      const outerCompositeItems = x.GetCompositeStatus.Content.Items;
      const innerCompositeItems = outerCompositeItems.map(this.extractStatus);

      const outerAttributeItems = x.GetAttributeStatus.Content.Items;
      const innerAttributeItems =
        outerAttributeItems
          .filter((y: any) => Boolean((y.Content.Items || []).length))
          .map((item: any) => this.extractStatus(item.Content.Items[0]));

      const allItems = innerCompositeItems.concat(innerAttributeItems);
      return allItems;

    }));

    return currentStatuses;
  }

  apfLimits(): Observable<any[]> {
    //return this.get<any[]>(`Timeline/ApfLimits`);
    return this.get<any>('tables/F1BlL8A_6d2dm0SBrg5RJa_Z7Q01xA01cuhkmah-3rqOPBcgT1JGLUNPR0VOQUZcQ0dNUFxUQUJMRVNbQVBGX0xJTUlUU10/data')
      .pipe(map(x => x.Rows));
  }


}

import { DataService } from 'src/app/api/data.service';
import { Injectable } from '@angular/core';
import {
  Observable,
  ReplaySubject,
  Subject,
  filter,
  BehaviorSubject
} from 'rxjs';
import { OAuthService } from 'angular-oauth2-oidc';
import { authCodeFlowConfig } from 'src/app/auth.config';

interface User {
  userid: string;
  email: string;
  firstname: string;
  lastname: string;
  name: string;
  isAdmin: boolean;
  roles: string[];
}
@Injectable({
  providedIn: 'root'
})
export class UserService {
  constructor(
    private oauthService: OAuthService,
    private dataService: DataService
  ) {
    this._currentUser$ = new ReplaySubject(1);

    if (this.oauthService.hasValidAccessToken()) {
      const profile = JSON.parse(window.sessionStorage.id_token_claims_obj);
      //this.oauthService.loadUserProfile().then((profile:any) => {
        this.dataService.currentUserRoles().subscribe((roles) => {
          this._currentUser$.next({
            userid: profile.userid,
            email: profile.email,
            firstname: profile.first_name,
            lastname: profile.last_name,
            name: profile.name,

            isAdmin: roles.some((r) => r === 'Admin'),
            roles
          });
        });
      // },(err)=>{
      //   console.error(err);
      // });
    }
  }

  private _currentUser$: Subject<User>;

  public Init(profile: any) {
    this.dataService.currentUserRoles().subscribe((roles) => {
      this._currentUser$.next({
        userid: profile.info.userid,
        email: profile.info.email,
        firstname: profile.info.first_name,
        lastname: profile.info.last_name,
        name: profile.info.name,

        isAdmin: roles.some((r) => r === 'Admin'),
        roles
      });
    });
  }

  public Login() {
    this.oauthService.configure(authCodeFlowConfig);
    this.oauthService.loadDiscoveryDocumentAndLogin();

    this.oauthService.events
      .pipe(filter((e) => e.type === 'token_received'))
      .subscribe((_) => {
        // console.debug('state', this.oauthService.state);
        this.oauthService.loadUserProfile().then((profile: any) => {
          this.dataService.currentUserRoles().subscribe((roles) => {
            this._currentUser$.next({
              userid: profile.info.userid,
              email: profile.info.email,
              firstname: profile.info.first_name,
              lastname: profile.info.last_name,
              name: profile.info.name,

              isAdmin: roles.some((r) => r === 'Admin'),
              roles
            });
          });
        });
        // const scopes = this.oauthService.getGrantedScopes();
        // console.debug('scopes', scopes);
      });
  }

  public get currentUser$(): Observable<User> {
    return this._currentUser$ as Observable<User>;
  }
}

